#define mul(a, b) ((a % HELL) * (b % HELL)) % HELL

vector<array<int, 2>> getNeighbours(int x, int y) {
	vector<array<int, 2>> neighs;
	neighs.pb({ x + 2 * y, y });
	neighs.pb({ x - 2 * y, y });
	if ((y + 2 * x) > 0) {
		neighs.pb({ x, y + 2 * x });
	} else {
		neighs.pb({ -x, -1 * (y + 2 * x) });
	}
	if ((y - 2 * x) > 0) {
		neighs.pb({ x, y - 2 * x });
	} else {
		neighs.pb({ -1 * x, -1 * (y - 2 * x) });
	}
	return neighs;
}

